SELECT id, key, title, description, thumbnail, pages, size, released_at, created_at,
    (
      SELECT json_agg(artist) FROM (
        SELECT json_build_object('name', name, 'slug', slug) AS artist
        FROM archive_artists JOIN artists ON id = artist_id WHERE archive_id = archives.id
      )
    ) AS artists,
    (
      SELECT json_agg(circle) FROM (
        SELECT json_build_object('name', name, 'slug', slug) AS circle
        FROM archive_circles JOIN circles ON id = circle_id WHERE archive_id = archives.id
      )
    ) AS circles,
    (
      SELECT json_agg(magazine) FROM (
        SELECT json_build_object('name', name, 'slug', slug) AS magazine
        FROM archive_magazines JOIN magazines ON id = magazine_id WHERE archive_id = archives.id
      )
    ) AS magazines,
    (
      SELECT json_agg(event) FROM (
        SELECT json_build_object('name', name, 'slug', slug) AS event
        FROM archive_events JOIN events ON id = event_id WHERE archive_id = archives.id
      )
    ) AS events,
    (
      SELECT json_agg(publisher) FROM (
        SELECT json_build_object('name', name, 'slug', slug) AS publisher
        FROM archive_publishers JOIN publishers ON id = publisher_id WHERE archive_id = archives.id
      )
    ) AS publishers,
    (
      SELECT json_agg(parody) FROM (
        SELECT json_build_object('name', name, 'slug', slug) AS parody
        FROM archive_parodies JOIN parodies ON id = parody_id WHERE archive_id = archives.id
      )
    ) AS parodies,
    (
      SELECT json_agg(tag) FROM (
        SELECT json_build_object('name', name, 'slug', slug, 'namespace', namespace) AS tag
        FROM archive_tags JOIN tags ON id = tag_id WHERE archive_id = archives.id ORDER BY name ASC
      )
    ) AS tags,
     (
      SELECT json_agg(source) FROM (
        SELECT json_build_object('name', name, 'url', url) AS source
        FROM archive_sources WHERE archive_id = archives.id
      )
    ) AS sources,
     (
      SELECT json_agg(image) FROM (
        SELECT json_build_object('filename', filename, 'page_number', page_number, 'width', width, 'height', height) AS image
        FROM archive_images WHERE archive_id = archives.id ORDER BY page_number ASC
      )
    ) AS images
    FROM archives WHERE id = $1
